INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T4_7', 'C040701', '4.7.1 Extent to which (i) global citizenship education and (ii) education for sustainable development, including gender equality and human rights, are mainstreamed at all levels in: (a) national education policies; (b) curricula; (c) teacher education; and (d) student assessment', '', '', '001', 'A', 'SDGS', GETDATE());
