INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T13_b', 'C130b01', '13.b.1 عدد أقل البلدان نمواً والدول الجزرية الصغيرة النامية التي تتلقى دعماً متخصصاً ومقدار الدعم المقدم، بما في ذلك التمويل والتكنولوجيا وبناء القدرات، لآليات تحسين مستوى قدرات التخطيط والإدارة الفعالين المتعلقين بتغير المناخ، بما في ذلك التركيز على النساء والشباب والمجتمعات المحلية والمهمشة', '', '', '3', '001', 'A', 'SDGS', GETDATE());
