INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('14', 'T14_2', 'TARGET code 14.2  - إدارة النظم الإيكولوجية البحرية والساحلية على نحو مستدام وحمايتها، من أجل تجنب حدوث آثار سلبية كبيرة، بوسائل منها تعزيز قدرتها على الصمود، واتخاذ الإجراءات اللازمة لإعادتها إلى ما كانت عليه من أجل تحقيق الصحة والإنتاجية للمحيطات، بحلول عام 2020', '', '', '2', '002', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('14', 'T14_2', 'TARGET code 14.2  - By 2020, sustainably manage and protect marine and coastal ecosystems to avoid significant adverse impacts, including by strengthening their resilience, and take action for their restoration in order to achieve healthy and productive oceans', '', '', '002', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T14_2', 'C140201', '14.2.1 نسبة المناطق الاقتصادية الحصرية الوطنية التي تُدار باستخدام نُهج قائمة على النظم الإيكولوجية', '', '', '3', '001', 'A', 'SDGS', GETDATE());
