INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('14', 'T14_a', 'TARGET code 14.a  - زيادة المعارف العلمية، وتطوير قدرات البحث، ونقل التكنولوجيا البحرية، مع مراعاة معايير اللجنة الأوقيانوغرافية الحكومية الدولية ومبادئها التوجيهية المتعلقة بنقل التكنولوجيا البحرية، من أجل تحسين صحة المحيطات، وتعزيز إسهام التنوع البيولوجي البحري في تنمية البلدان النامية، ولا سيما الدول الجزرية الصغيرة النامية وأقل البلدان نموا', '', '', '2', '008', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('14', 'T14_a', 'TARGET code 14.a  - Increase scientific knowledge, develop research capacity and transfer marine technology, taking into account the Intergovernmental Oceanographic Commission Criteria and Guidelines on the Transfer of Marine Technology, in order to improve ocean health and to enhance the contribution of marine biodiversity to the development of developing countries, in particular small island developing States and least developed countries', '', '', '008', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T14_a', 'C140a01', '14.a.1 نسبة مجموع الميزانية المخصصة للبحوث في مجال التكنولوجيا البحرية', '', '', '3', '001', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T14_a', 'C140a01', '14.a.1 Proportion of total research budget allocated to research in the field of marine technology', '', '', '001', 'A', 'SDGS', GETDATE());
