INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('14', 'T14_b', 'TARGET code 14.b  - Provide access for small-scale artisanal fishers to marine resources and markets', '', '', '009', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T14_b', 'C140b01', '14.b.1 مدى تطبيق إطار قانوني/تنظيمي/سياساتي/مؤسسي يعترف بحقوق مصائد الأسماك الصغيرة في الوصول إلى الموارد البحرية ويحمي تلك الحقوق', '', '', '3', '001', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T14_b', 'C140b01', '14.b.1 Progress by countries in the degree of application of a legal/regulatory/policy/institutional framework which recognizes and protects access rights for small-scale fisheries', '', '', '001', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('14', 'T14_c', 'TARGET code 14.c  - تعزيز حفظ المحيطات ومواردها واستخدامها استخداما مستداما بتنفيذ القانون الدولي بصيغته الواردة في اتفاقية الأمم المتحدة لقانون البحار التي تضع الإطار القانوني لحفظ المحيطات ومواردها واستخدامها على نحو مستدام، على نحو ما تشير إليه الفقرة 158 من وثيقة ”المستقبل الذي نصبو إليه“', '', '', '2', '010', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('14', 'T14_c', 'TARGET code 14.c  - Enhance the conservation and sustainable use of oceans and their resources by implementing international law as reflected in the United Nations Convention on the Law of the Sea, which provides the legal framework for the conservation and sustainable use of oceans and their resources, as recalled in paragraph 158 of “The future we want”', '', '', '010', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, LEVELNO, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T14_c', 'C140c01', '14.c.1 عدد البلدان التي تحرز تقدماً في التصديق على صكوك متعلقة بالمحيطات تنفذ القانون الدولي، وفي قبول تلك الصكوك وتنفيذها بموجب أطر قانونية وسياساتية ومؤسسية، على النحو الوارد في اتفاقية الأمم المتحدة لقانون البحار، من أجل حفظ المحيطات ومواردها، واستخدامها على نحو مستدام', '', '', '3', '001', 'A', 'SDGS', GETDATE());
INSERT INTO "MENUSELECTION_ENG"(MENU, SELECTION, PRESTEXT, PRESTEXTS, DESCRIPTION, SORTCODE, PRESENTATION, USERID, LOGDATE) VALUES ('T14_c', 'C140c01', '14.c.1 Number of countries making progress in ratifying, accepting and implementing through legal, policy and institutional frameworks, ocean-related instruments that implement international law, as reflected in the United Nation Convention on the Law of the Sea, for the conservation and sustainable use of the oceans and their resources', '', '', '001', 'A', 'SDGS', GETDATE());
